function initPageTransitions(){barba.hooks.before(()=>{window.scrollTo({top:0})}),barba.init({sync:!1,debug:!1,timeout:7e3,transitions:[{name:"default",once({next:t}){updateBodyClass(t.html),initScript()},async leave(t){initBarbaNavUpdate(t),await gsap.to(t.current.container,{opacity:0,duration:.5}),t.current.container.remove()},async enter({next:t}){return updateBodyClass(t.html),gsap.from(t.container,{opacity:0,duration:.5})},async beforeEnter({next:t}){updateBodyClass(t.html),initScript()}}]})}function updateBodyClass(t){t=t.match(/<body.+?class="([^"]*)"/i);document.body.setAttribute("class",t?t[1]:"")}function delay(n=2e3){return new Promise(t=>setTimeout(t,n))}function initScript(){initBarbaNavUpdate(),initWindowInnerheight(),initSwiperSlider()}function initBarbaNavUpdate(t){if(t&&t.next.html){const n=$(t.next.html).find("[data-barba-update]");$("[data-barba-update]").each(function(t){0<$(n[t]).length&&(t=$(n[t]).attr("data-link-status"),$(this).attr("data-link-status",t))})}}function initWindowInnerheight(){$(document).ready(()=>{var t=.01*window.innerHeight;document.documentElement.style.setProperty("--vh",t+"px")})}function initSwiperSlider(){new Swiper(".blog-slider",{slidesPerView:2,loop:!0,spaceBetween:20,navigation:{nextEl:".btn-next",prevEl:".btn-prev"},breakpoints:{1200:{slidesPerView:3}},autoplay:{delay:2500,disableOnInteraction:!0}})}initPageTransitions();